  <script>
  import axios from 'axios';
  export default {
    data() {
      return {
        essayList: []
      }
    },
    async mounted() {
      const result = await axios.get('http://127.0.0.1:3000/get', {
      })
      let dataList = [];
      // console.log(result)
      // console.log(result.data);
      result.data.data.map(val => {
        var dataItem = {};
        var zongList = [];
        let iii = 0;
        if (val.content["0"]) {
          zongList[0] = {
			type: 0,
			cont: val.content["0"]
		  };
        } else {
		  zongList[0] = {
			type: 1,
			cont: val.imageList["0"]
		  };
          iii++;
        }
        let zongLength = Object.keys(val.content).length + Object.keys(val.imageList).length;
		console.log(zongLength)
        for (let i = 1; i < zongLength; i++) {
          if (val.content[i.toString()]) {
            zongList[i] = {
				type: 0,
				cont: val.content[i.toString()]
			};
          } else {
			zongList[i] = {
				type: 1,
				cont: val.imageList[iii.toString()]
			};
            iii++;
          }
        }
        dataItem.title = val.title;
        dataItem.id = val.id;
        dataItem.zongList = zongList;
        dataItem.createDate = val.create;
        dataList.push(dataItem);
      })
      // console.log("kjxgcviuxcv908");
      console.log(dataList);
	  this.essayList = dataList;
    },
    methods: {

    }
  }
</script>
