<template>
    <div class="hello">
        <div class="contentItem" v-for="(item, index) in contentList" :key="index">
          <div class="fininshed" v-if="!item.editStatus && !item.type" @click="startEditing(index)">
            {{item.content}}
          </div>
          <div class="inputBox" v-if="item.editStatus && !item.type">
            <!-- <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea> -->
            <textarea type="text" style="height: auto;" class="form-control" :ref="index.toString()" v-model="item.contentTemp" @blur="stopEditing(index)" @keyup.enter="stopEditing(index)"></textarea>
          </div>
          <div class="inputBox" v-if="item.type">
            <div class="image-container">
              <span class="close-button" @click="removeImage(index)">X</span>
              <img :src="item.content" alt="Selected Image" />
            </div>
          </div>
        </div>
        <div class="alert alert-success" role="alert" style="margin-top: 30px;">
            <!-- A simple success alert—check it out! -->
            <div>

            </div>
