  <script>
  import axios from 'axios';
  export default {
    name: 'HelloWorld',
    props: {
      msg: String
    },
    data() {
      return {
        text: 'Click to edit',
        editedText: '',
        editing: false,
        selectedImage: null,
        contentList: [
          {
            type: 0,
            content: "",
            contentTemp: "",
            editStatus: false
          }
        ]
      };
    },
    methods: {
      async submit() {
        console.log(this.contentList);
        let textContent = {};
        let formData = new FormData()
        for (let i = 0; i < this.contentList.length; i++) {
          if (this.contentList[i].type == 1) {
            formData.append('pictures', this.contentList[i].data);
          } else {
            textContent[i] = this.contentList[i].content;
          }
        }
        formData.append('textContent', JSON.stringify(textContent));
        console.log(formData);
        console.log("989007");
        //上传单个文件时
        //this.files.forEach(element => {
        //	formData.append('pictures', element)
        //})
        //上传多个文件时
        // 当只上传普通数据类型时，可以直接传this.userInfor这个对象，但是当上传混合数据类型时，必须将对象通过JSON.stringfy()转化为可读取的字符串，否则到了后端会无法读取[object Object]。
        const result = await axios.post('http://127.0.0.1:3000/xxx', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
            //一定不要忘了配置请求头中的数据类型
          },
        })
        console.log(result)
      },
      startOne() {
        if (!this.contentList[this.contentList.length - 1].content) {
          this.startEditing(this.contentList.length - 1);
        } else {
          this.contentList.push(
            {
              type: 0,
              content: "",
              contentTemp: "",
              editStatus: false
            }
          );
          this.startEditing(this.contentList.length - 1);
        }
        // if (this.contentList.length == 1) {
        //   if (!this.contentList[0].content) {
        //     this.startEditing(0);
        //   } else {
        //     this.contentList.push(
        //       {
        //         type: 0,
        //         content: "",
        //         contentTemp: "",
        //         editStatus: false
        //       }
        //     );
        //     this.startEditing(1);
        //   }
        // } else {
        //   if (!this.contentList[this.contentList.length - 1].content) {
        //     this.startEditing(this.contentList.length - 1);
        //   } else {
        //     this.contentList.push(
        //       {
        //         type: 0,
        //         content: "",
        //         contentTemp: "",
        //         editStatus: false
        //       }
        //     );
        //     this.startEditing(this.contentList.length - 1);
        //   }
        // }
      },
      openImageDialog() {
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = 'image/*';
        input.onchange = (event) => {
          const file = event.target.files[0];
          if (file) {
            const reader = new FileReader();
            reader.onload = (e) => {
              this.contentList[this.contentList.length - 1].type = 1;
              this.contentList[this.contentList.length - 1].content = e.target.result;
              this.contentList[this.contentList.length - 1].data = file;
              this.contentList.push(
                {
                  type: 0,
                  content: "",
                  contentTemp: "",
                  editStatus: false
                }
              );
            };
            reader.readAsDataURL(file);
          }
        };
        input.click();
      },
      removeImage(index) {
        this.contentList[index].content = null;
        this.contentList.splice(index, 1);
      },
      startEditing(index) {
        this.contentList[index].editStatus = true;
        this.contentList[index].contentTemp = this.contentList[index].content;
        this.$nextTick(() => {
          console.log("khxcbvbv");
          console.log(this.$refs);
          this.$refs[index.toString()][0].focus();
        });
      },
      stopEditing(index) {
        this.contentList[index].editStatus = false;
        this.contentList[index].content = this.contentList[index].contentTemp;
        if (!this.contentList[index].content) {
          this.contentList.splice(index, 1);
        }
        if (this.contentList[this.contentList.length - 1].content) {
          this.contentList.push(
            {
              type: 0,
              content: "",
              contentTemp: "",
              editStatus: false
            }
          );
        }
      }
    }
  }
  </script>
  
